<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n    <meta charset=\"utf-8\">\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n    <title>Unauthorized</title>\n    <style>\n        body {\n            font-family: 'Nunito', sans-serif;\n            background-color: #f8fafc;\n            color: #636b6f;\n            font-weight: 100;\n            height: 100vh;\n            margin: 0;\n            display: flex;\n            align-items: center;\n            justify-content: center;\n            flex-direction: column;\n        }\n        .container {\n            text-align: center;\n        }\n        .title {\n            font-size: 36px;\n            margin-bottom: 20px;\n        }\n        .message {\n            font-size: 18px;\n            margin-bottom: 20px;\n        }\n        .countdown {\n            font-size: 16px;\n            color: #007bff;\n        }\n    </style>\n</head>\n<body>\n    <div class=\"container\">\n        <div class=\"title\">401 - Unauthorized</div>\n        <div class=\"message\">Your session has expired. You will be redirected to login.</div>\n        <div class=\"countdown\">Redirecting in <span id=\"countdown\">3</span> seconds...</div>\n    </div>\n    \n    <script>\n        let seconds = 3;\n        const countdownElement = document.getElementById('countdown');\n        \n        const timer = setInterval(function() {\n            seconds--;\n            countdownElement.textContent = seconds;\n            \n            if (seconds <= 0) {\n                clearInterval(timer);\n                window.location.replace('/login');\n            }\n        }, 1000);\n        \n        // Also redirect immediately if user clicks anywhere\n        document.addEventListener('click', function() {\n            window.location.replace('/login');\n        });\n    </script>\n</body>\n</html>\n